import streamlit as st
import pickle
import numpy as np
import pandas as pd
from sklearn.preprocessing import StandardScaler

ridge_model = pickle.load(open('models/ridge.pkl', 'rb'))
standard_scaler = pickle.load(open('models/scaler.pkl', 'rb'))

import streamlit as st

original_title = '<h1 style="font-family: serif; color:white; font-size: 50px;">Algerian Forest Fire Prediction </h1>'
st.markdown(original_title, unsafe_allow_html=True)


# Set the background image
background_image = """
<style>
[data-testid="stAppViewContainer"] > .main {
    background-image: url("data:image/jpeg;base64,");
    background-size: 100vw 100vh;  # This sets the size to cover 100% of the viewport width and height
    background-position: center;  
    background-repeat: no-repeat;
}
</style>
"""

st.markdown(background_image, unsafe_allow_html=True)

input_style = """
<style>
input[type="text"] {
    background-color: transparent;
    color: #a19eae;  // This changes the text color inside the input box
}
div[data-baseweb="base-input"] {
    background-color: transparent !important;
}
[data-testid="stAppViewContainer"] {
    background-color: transparent !important;
}
</style>
"""
st.markdown(input_style, unsafe_allow_html=True)

Temperature = st.number_input("Enter the Temperature in Celcius:", format="%.2f")
RH = st.number_input("Enter the Relative Humidity:", format="%.2f")
Ws = st.number_input("Enter the Wind Speed in km/hr:", format="%.2f")
Rain = st.number_input("Enter the Rainfall in mm:", format="%.2f")
FFMC = st.number_input("Enter the Fine Fuel Moisture Code:", format="%.2f")
DMC = st.number_input("Enter the Duff Moisture Code:", format="%.2f")
ISI = st.number_input("Enter the Initial Spread Index:", format="%.2f")
Classes = st.selectbox("Enter the Classes:", options=["Fire","Not Fire"])
Region = st.selectbox("Enter the Region:", options=["Bejaia Region","Sidi-Bel Abbes Region"])

if(Region == "Bejaia Region Dataset"):
    Region = 1
else:
    Region = 0

if (Classes == "Fire"):
    Classes = 0
else:
    Classes = 1

new_data_scaled=standard_scaler.transform([[Temperature,RH,Ws,Rain,FFMC,DMC,ISI,Classes,Region]])
result = ridge_model.predict(new_data_scaled)

if(st.button("Predict")):
    st.markdown('The Forest Weather Index is '+ str(result[0]))
